          int scale = pu.cu->altLMParaUnit.scale[0];
          int shift = 5;
          int offset = pu.cu->altLMParaUnit.offset[0];
          pcBufPredRefTop.Y().linearTransform(scale, shift, offset, true, pu.cu->slice->clpRng(COMPONENT_Y));
