    affMrgCtxTmp.copyLICParamFromCtx(uiMergeCand, affMrgCtx, uiMergeCand);
#endif
#endif
#if JVET_AK0095_ENHANCED_AFFINE_CANDIDATE
    affMrgCtxTmp.m_isConstructed[uiMergeCand] = affMrgCtx.m_isConstructed[uiMergeCand];
#endif
